import React from 'react';
import { Descriptions, Image } from 'antd';
import styles from './index.less';

/**
 *
 * 描述列表封装
 * 2020年8月4日 15:35:24 Dong
 *
 * @formItems 表单内容数组
 * @initialValues 默认值
 */

const DescriptionsCondition = ({
  formItems = [],
  title,
  initialValues,
  column,
  style,
  children,
}) => {
  /**
   * 查看图片
   * @param {*} fileObj 图片路径
   */
  const imgShow = (fileObj = '') => {
    if (fileObj.indexOf(',') > -1) {
      fileObj = fileObj.split(',');
    }
    let imgComponent = '';
    const imgCom = (url) => (
      <Image
        key={url}
        width={104}
        height={104}
        src={url}
        className={styles.descript_img}
        fallback="data:image/png;base64,"
      />
    );
    if (Array.isArray(fileObj)) {
      imgComponent = fileObj.map((url) => imgCom(url));
    } else if (fileObj.length > 0) {
      imgComponent = imgCom(fileObj);
    }
    return <Image.PreviewGroup>{imgComponent}</Image.PreviewGroup>;
  };

  // 遍历表单
  const getFields = () =>
    formItems.map((item, i) => {
      // visible true 则显示 hidden false 显示 show true显示
      // visible 和表单共用 hidden 和表单相反 show 独有
      const { show = true, visible = true, hidden = false } = item;
      const valueKey = item.childrenName || item.name;
      return (
        show &&
        visible &&
        !hidden && (
          <Descriptions.Item
            span={item.span}
            label={item.label}
            key={`${item.label}${i}`}
            className={styles.descriptions_item}
          >
            {initialValues && Object.keys(initialValues).length
              ? item.render
                ? item.render(
                    Array.isArray(valueKey)
                      ? initialValues[valueKey[0]][valueKey[1]]
                      : initialValues[valueKey],
                    initialValues,
                  )
                : item.type === 'upload'
                ? imgShow(
                    item.initialValue
                      ? item.initialValue
                      : Array.isArray(valueKey)
                      ? initialValues[valueKey[0]][valueKey[1]]
                      : initialValues[valueKey],
                  )
                : item.initialValue
                ? item.initialValue
                : Array.isArray(valueKey)
                ? initialValues[valueKey[0]][valueKey[1]]
                : initialValues[valueKey]
              : ''}
            {item.children && <div>{item.children}</div>}
          </Descriptions.Item>
        )
      );
    });

  return (
    <>
      <Descriptions
        title={title}
        className={styles.descriptions_box}
        bordered
        column={column || 1}
        size="small"
        style={style}
      >
        {formItems.length ? getFields() : ''}
      </Descriptions>
      {children}
    </>
  );
};

export default DescriptionsCondition;
